tinymce.PluginManager.add('quickjump', function(editor, url) {
    var openDialog = function () {
      return editor.windowManager.open({
        title: 'Link to other entries',
        body: {
          type: 'panel',
          items: [
            {
              type: 'input',
              name: 'title',
              label: 'Entry name'
            }
          ]
        },
        buttons: [
          {
            type: 'cancel',
            text: 'Close'
          },
          {
            type: 'submit',
            text: 'Save',
            primary: true
          }
        ],
        onSubmit: function (api) {
          var data = api.getData();
          // 将输入框内容插入到内容区光标位置
          var url = "../EntryInfo/"+data.title
          console.log(data)
          urlbody={href:url,rel:null,target:null}
          editor.insertContent(editor.dom.createHTML("a",urlbody,editor.dom.encode(data.title)))
          api.close();
        }
      });
    };
    
    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton('quickjump', {
      text: 'Link',
      onAction: function () {
        openDialog();
      }
    });
  
    // 注册一个菜单项名称 menu/menubar
    editor.ui.registry.addMenuItem('quickjump', {
      text: 'Link',
      onAction: function() {
        openDialog();
      }
    });
  
    return {
      getMetadata: function () {
        return  {
          //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
          name: "Quickjump plugin",//插件名称
          url: "http://exampleplugindocsurl.com", //作者网址
        };
      }
    };
  });